import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';

const styles = {
  main: {
    width:"30%", 
    background:"#F2F1F9", 
    border:"none", 
    padding:"0.5rem"
  },
  searchIcon: {
    height: 30,
    border: "none",
    cursor: "pointer"
  }
};

const CheckBox = ({ setValue, placeholder, classes }) => {
  const [query, setQuery] = useState('');
  const [stateFilter, setStateFilter] = useState('all');

  const typeArray=[
    'small',
    'medium',
    'large',
    'heliport',

