  useEffect(() => {
    let filteredRestaurants = filterDataByGenre(restaurants);
    filteredRestaurants = filterDataByState(filteredRestaurants);
    setFilteredRestaurants(filteredRestaurants);
  }, [genreFilter, stateFilter]);

  const filterDataByState = data => {
    if (stateFilter !== 'all') {
      return data.filter(row => row.state.includes(stateFilter));
    }
    return data;
  }

  const renderTypeOptions = () => ['all', ...typeArray];




  const handleFilterChange = e => {

      if(e.target.checked)
      setValue(e.target.value)
      else
      setValue(query)

    }

  return (
    typeArray.map(filter=>(
      <Fragment>
      <label htmlFor={filter.id}>{filter.type}</label>
      <input type="checkbox"
      id={filter.id}
        value={filter.type}
        placeholder={placeholder}
        onClick={handleFilterChange}

      />

    </Fragment>
    ))

  );
}

export default CheckBox;
