import React, { useState, useEffect } from 'react';
import SearchBar from './SearchBar';
import TablePagination from './TablePagination';
import CheckBox from './CheckBox';



const Table = ({ renderRow, entries, header, classes, placeholder = '', textfilterOptions = [],checklistoptions = [], pageLimit }) => {
  const [filter, setFilter] = useState('');
  const [page, setPage] = useState(0);

  const [ filterList, activeFilter ] = useState('');





  const doesIncludeEntry = entry => {

    return filter.length > 0 ? textfilterOptions.filter(option => entry[option].toString().toLowerCase().includes(filter.toLowerCase())).length > 0 : true;
  };

  const getPaginatedEntries = entries => {

    const offset = page * pageLimit;
    return entries.slice(offset, offset + pageLimit);
  }

  const getFilteredEntries = () => {
    return entries.filter(entry => doesIncludeEntry(entry));
  }

  const renderEntries = () => {

    return getPaginatedEntries(getFilteredEntries());
  }


  const onFilterChange=filter=> {
    const { filterList, activeFilter } = this.state;

      if (activeFilter.includes(filter)) {
        const filterIndex = activeFilter.indexOf(filter);
        const newFilter = [...activeFilter];
        newFilter.splice(filterIndex, 1);
        this.setState({ activeFilter: newFilter });
      } else {
        this.setState({ activeFilter: [...activeFilter, filter] });
      }

  }
  const rendercheckoptions=() =>{
    if (activeFilter.includes(filter)) {

      const filterIndex = activeFilter.indexOf(filter);
      const newFilter = [...activeFilter];
      newFilter.splice(filterIndex, 1);
      this.setState({ activeFilter: newFilter });
    } else {
      this.setState({ activeFilter: [...activeFilter, filter] });
    }
  }



  const typeArray=[
    {id:1,type:"small"},
    {id:2,type:"medium"},
    {id:3,type:"large"},
    {id:4,type:"heliport"},

  ]
  return (
    <div>

     <CheckBox
      setValue={setFilter}
     />


      <SearchBar 
        setValue={setFilter}
        placeholder={placeholder}
      />
      <table >
        {header}
        {renderEntries().map(entry => renderRow(entry))}
      </table>
      <TablePagination 
        pageLimit={pageLimit}
        pages={getFilteredEntries().length}
        currentPage={page}
        setPage={setPage}
      />
    </div>
  )
}

export default Table;
