import React, { useState, useEffect } from 'react';
import SearchBar from './SearchBar';
import TablePagination from './TablePagination';
import CheckBox from './CheckBox';



const Table = ({ renderRow, entries, header, classes, placeholder = '', textfilterOptions = [], pageLimit }) => {
  const [filter, setFilter] = useState('');
  const [page, setPage] = useState(0);

  const doesIncludeEntry = entry => {
    return filter.length > 0 ? textfilterOptions.filter(option => entry[option].toString().toLowerCase().includes(filter.toLowerCase())).length > 0 : true;
  };

  const getPaginatedEntries = entries => {

    const offset = page * pageLimit;
    return entries.slice(offset, offset + pageLimit);
  }

  const getFilteredEntries = () => {
    return entries.filter(entry => doesIncludeEntry(entry));
  }

  const renderEntries = () => {
    return getPaginatedEntries(getFilteredEntries());
  }

  // const renderTypeOptions = () => ['all', ...STATES_ABBR];

  const [typeFilter, setTypeFilter] = useState('all');

  useEffect(() => {
    let filteredrows = filterDataByGenre(restaurants);
    filteredRestaurants = filterDataByState(filteredRestaurants);
    setFilteredRestaurants(filteredRestaurants);
  }, [genreFilter, stateFilter]);

  const filterDataByState = data => {
    if (stateFilter !== 'all') {
      return data.filter(row => row.state.includes(stateFilter));
    }
    return data;
  }
  const typeArray=[
    {id:1,type:"small"},
    {id:2,type:"medium"},
    {id:3,type:"large"},
    {id:4,type:"heliport"},

  ]
  return (
    <div>

     <CheckBox
      setValue={setFilter}
     />


      <SearchBar 
        setValue={setFilter}
        placeholder={placeholder}
      />
      <table >
        {header}
        {renderEntries().map(entry => renderRow(entry))}
      </table>
      <TablePagination 
        pageLimit={pageLimit}
        pages={getFilteredEntries().length}
        currentPage={page}
        setPage={setPage}
      />
    </div>
  )
}

export default Table;
