import React, { Fragment } from 'react';

const styles = {
  main: {
    border: '1px solid #dddddd',
    textAlign: 'right',
    padding: 8,
    paddingLeft: 0
  },
  page: {
    border: '1px solid #dddddd',
    padding: 8,
    cursor: 'pointer'
  }
};

const TablePagination = ({ currentPage, pages, setPage, pageLimit, classes }) => {
  const numPages = Math.ceil(pages / pageLimit);

  const getPaginationNumbers = () => {
    const blocks = [];
    for (let i = 0; i < numPages; i++) {
      blocks.push(i);
    }
    return blocks;
  };

  const renderPageBlocks = () => {
    const getPageNumbers = getPaginationNumbers();

    return getPageNumbers.map(pageNum =>
      <a 
        key={pageNum} 

        onClick={() => setPage(pageNum)}

      >
      {pageNum === currentPage ? (pageNum+1) + " of " + numPages + " results" : ""}
      </a>
    );
  };

  const goToPrevPage = () => {
    if (currentPage > 0) {
      setPage(currentPage - 1);
    }
  };

  const goToNextPage = () => {
    if (currentPage < numPages - 1) {
      setPage(currentPage + 1);
    }
  };

  const renderPrevPageBlocks = () => {
    return (
      <Fragment>
        <a key="prev-page" id="prev" onClick={goToPrevPage}>&#8592;</a>
      </Fragment>
    )
  };

  const renderNextPageBlocks = () => {
    return (
      <Fragment>
        <a key="next-page" id="next" onClick={goToNextPage}>&rarr;</a>
      </Fragment>
    )
  };

  return (
    <div class="pagination">
      {renderPrevPageBlocks()}
     showing {renderPageBlocks()}of{numPages}
      {renderNextPageBlocks()}
    </div>
  );
}

export default TablePagination;
