export const getRestaurants = () => {
  return new Promise(resolve => {
    fetch('https://code-challenge.spectrumtoolbox.com/api/restaurants', {
        headers: {
          Authorization: 'Api-Key q3MNxtfep8Gt',
        }
      }).then(res => res.json())
      .then(data => resolve(data));
  });
}
const getData=()=>{
