import React, { useEffect, useState } from 'react';
import { getRestaurants } from '../apis/restaurants';
import Table from '../components/Table';
import { STATES_ABBR } from '../utils/constants';

const styles = {
  contentSection: {
    border: '1px solid #dddddd',
    textAlign: 'left',
    padding: 8
  },
  headerSection: {
    border: '1px solid #dddddd',
    textAlign: 'left',
    padding: 8
  }
}

const MainPage = ({ classes }) => {

  const [restaurants, setRestaurants] = useState([]);
  const [getFilteredRestaurants, setFilteredRestaurants] = useState([]);

  useEffect(() => {
    getRestaurants().then(data => {
      const sortData = data.sort((a, b) => (a.name > b.name) ? 1 : -1)
      setRestaurants(sortData);
      setFilteredRestaurants(sortData);
    });
  }, []);


  const headerOptions = [
    {
      id: "name"
    },
    {
      id: "icao"
       },
    {
      id: "iata"
       },
    {
      id: "elevation"

    },
    {
      id: "latitude"

    },
    {
      id: "longitude"

    },
    {
      id: "type"
       }];


  const generateRow = row => {
    return (
      <tr key={row.id}>
        {headerOptions.map(field => (
          <td >
            {row[field.id]}
          </td>
        ))}
      </tr>
    )
  };

  const header = (
    <tr>
      {headerOptions.map(header => (
        <th >
          {header.id}
          {header.filterOptions && (
            <div>
              {header.filterOptions()}
            </div>
          )}
        </th>
      ))}
    </tr>
  );

  return (

      <Table
        renderRow={row => generateRow(row)}
        entries={getFilteredRestaurants}
        header={header}
        placeholder="Search"
        textfilterOptions={['name','icao','iata','elevation','latitude','longitude','type']}
        pageLimit={4}
      />

  )
}

export default MainPage;
